package week1Dharu.day1;

public class LearnOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=6;
		
		//Arithmetic Operators
		//add '+'
		System.out.println(a+b);
		//subtract '-'
		System.out.println(a-b);
		//multiplication '*'
		System.out.println(a*b);
		//reminder '%'
		System.out.println(a%b);
		// division '/' (quotient)
		System.out.println(a/b);
		
		//Comparison Operators
		// a is greater than b '>'
		System.out.println(a>b);
		
		// a is less than b '<'
		System.out.println(a<b);
		
		// a is equal to b '=='
		System.out.println(a==b);
		
		//a is not equal to b '!='
		System.out.println(a!=b);
		
		//Assignment Operators
		int c=5;
		
	}

}
